interface IForeignGivethBridge {
    event Deposit(address indexed sender, address token, uint amount, bytes32 homeTx, bytes data);
    event Withdraw(address indexed recipient, address token, uint amount);
    event TokenAdded(address indexed mainToken, address sideToken);

    function withdraw(address sideToken, uint amount) external;
    function withdraw(address recipient, address sideToken, uint amount) public;

    function deposit(address sender, address mainToken, uint amount, bytes32 homeTx, bytes data) external;
    function addToken(address mainToken, string tokenName, uint8 decimals, string tokenSymbol) external;
}","keccak256":"0x25fee627960da0977555015aba94373e56da5ddcc07d0d66724f0427344617af"},"./contracts/lib/Pausable.sol":{"content":"pragma solidity ^0.4.21;

import "giveth-common-contracts/contracts/Owned.sol";

/**
 * @title Pausable
 * @dev Base contract which allows children to implement an emergency stop mechanism.
 */
contract Pausable is Owned {
    event Pause();
    event Unpause();

    bool public paused = false;

    /**
    * @dev Modifier to make a function callable only when the contract is not paused.
    */
    modifier whenNotPaused() {
        require(!paused);
        _;
    }

    /**
    * @dev Modifier to make a function callable only when the contract is paused.
    */
    modifier whenPaused() {
        require(paused);
        _;
    }

    /**
    * @dev called by the owner to pause, triggers stopped state
    */
    function pause() onlyOwner whenNotPaused public {
        paused = true;
        emit Pause();
    }

    /**
    * @dev called by the owner to unpause, returns to normal state
    */
    function unpause() onlyOwner whenPaused public {
        paused = false;
        emit Unpause();
    }
}","keccak256":"0xd134809350f024c16096d995915c6a348d770e1ec633723419f4f3acbd1e4b46"},"giveth-common-contracts/contracts/ERC20.sol":{"content":"pragma solidity ^0.4.19;


/**
 * @title ERC20
 * @dev A standard interface for tokens.
 * @dev https://github.com/ethereum/EIPs/blob/master/EIPS/eip-20-token-standard.md
 */
contract ERC20 {

    /// @dev Returns the total token supply
    function totalSupply() public constant returns (uint256 supply);

    /// @dev Returns the account balance of the account with address _owner
    function balanceOf(address _owner) public constant returns (uint256 balance);

    /// @dev Transfers _value number of tokens to address _to
    function transfer(address _to, uint256 _value) public returns (bool success);

    /// @dev Transfers _value number of tokens from address _from to address _to
    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success);

    /// @dev Allows _spender to withdraw from the msg.sender's account up to the _value amount
    function approve(address _spender, uint256 _value) public returns (bool success);

    /// @dev Returns the amount which _spender is still allowed to withdraw from _owner
    function allowance(address _owner, address _spender) public constant returns (uint256 remaining);

    event Transfer(address indexed _from, address indexed _to, uint256 _value);
    event Approval(address indexed _owner, address indexed _spender, uint256 _value);

}
